import React, { useState } from 'react';
import { Form, Button, Container, Row, Col, Modal } from 'react-bootstrap';
import axios from 'axios';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showSuccessModal, setShowSuccessModal] = useState(false);


    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://103.186.185.127:8082/userlogin', {
                email: email,
                password: password,
            });

            const roleId = response.data.data.userInfo.roleId;

            if (roleId === 300) {
                setLoginMessage('Successful Login! Welcome Customer');
            } else if (roleId === 103 || roleId === 104) {
                setLoginMessage('Successful Login! Welcome Admin');
            } else {
                setLoginMessage('Unknown Role');
            }
        } catch (error) {
            console.error('Login failed:', error);
            setLoginMessage('Login failed. Please try again.');
        }
    };

    return (
        <Container>
            <Row className="justify-content-center align-items-center min-vh-100">
                <Col md={6}>
                    <div className="login-box">
                        <Form onSubmit={handleSubmit}>
                            <Form.Group controlId="formEmail">
                                <Form.Label>Email</Form.Label>
                                <Form.Control
                                    type="email"
                                    placeholder="Enter email"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                />
                            </Form.Group>

                            <Form.Group controlId="formPassword">
                                <Form.Label>Password</Form.Label>
                                <Form.Control
                                    type="password"
                                    placeholder="Password"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                />
                            </Form.Group>

                            <Button className="mt-2" variant="primary" type="submit">
                                Login
                            </Button>
                        </Form>
                        <div className="mt-3">
                            {loginMessage && (
                                <div className={`alert ${loginMessage.includes('Success') ? 'alert-success' : 'alert-danger'}`}>
                                    {loginMessage}
                                </div>
                            )}
                        </div>
                    </div>
                </Col>
            </Row>
        </Container>
    );
}

export default Login;
