import React, { useState } from 'react';
import { Form, Button, Container, Row, Col, Modal } from 'react-bootstrap';
import axios from 'axios';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showSuccessModal, setShowSuccessModal] = useState(false);
    const [roleId, setRoleId] = useState(null);



    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://103.186.185.127:8082/userlogin', {
                email: email,
                password: password,
            });

            const roleId = response.data.data.userInfo.roleId;
            setRoleId(roleId);

            if (roleId === 300) {
                setShowSuccessModal('Successful Login! Welcome Customer');
            } else if (roleId === 103 || roleId === 104) {
                setShowSuccessModal('Successful Login! Welcome Admin');
            } else {
                setShowSuccessModal('Unknown Role');
            }
        } catch (error) {
            console.error('Login failed:', error);
            setShowSuccessModal('Login failed. Please try again.');
        }
    };

    return (
        <Container>
            <Row className="justify-content-center align-items-center min-vh-100">
                <Col md={6}>
                    <div className="login-box">
                        <Form onSubmit={handleSubmit}>
                            <Form.Group controlId="formEmail">
                                <Form.Label>Email</Form.Label>
                                <Form.Control
                                    type="email"
                                    placeholder="Enter email"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                />
                            </Form.Group>

                            <Form.Group controlId="formPassword">
                                <Form.Label>Password</Form.Label>
                                <Form.Control
                                    type="password"
                                    placeholder="Password"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                />
                            </Form.Group>

                            <Button className="mt-2" variant="primary" type="submit">
                                Login
                            </Button>
                        </Form>
                        <Modal show={showSuccessModal} onHide={() => setShowSuccessModal(false)}>
                            <Modal.Header closeButton>
                                <Modal.Title>Success!</Modal.Title>
                            </Modal.Header>
                            <Modal.Body>
                                {roleId === 300 ? 'Welcome Customer' : 'Welcome Admin'}
                            </Modal.Body>
                            <Modal.Footer>
                                <Button variant="primary" onClick={() => setShowSuccessModal(false)}>
                                    Close
                                </Button>
                            </Modal.Footer>
                        </Modal>
                    </div>
                </Col>
            </Row>
        </Container>
    );
}

export default Login;
