import React, { useState } from 'react';
import { Form, Button, Container, Row, Col, Modal } from 'react-bootstrap';
import Image from 'react-bootstrap/Image';
import axios from 'axios';
import chef from '../Images/chef.jpeg';


function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showSuccessModal, setShowSuccessModal] = useState(false);
    const [roleId, setRoleId] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');


    const handleResetForm = () => {
        setEmail('');
        setPassword('');
        setShowSuccessModal(false);
        setErrorMessage('');
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!email || !password) {
            setErrorMessage('Please fill in both email and password fields.');
            return;
        }
        // Clear any previous error messages
        setErrorMessage('');

        try {
            const response = await axios.post('http://103.186.185.127:8082/userlogin', {
                email: email,
                password: password,
            });

            const roleId = response.data.data.userInfo.roleId;
            setRoleId(roleId);

            if (roleId === 300) {
                setShowSuccessModal('Successful Login! Welcome Customer');
            } else if (roleId === 103 || roleId === 104) {
                setShowSuccessModal('Successful Login! Welcome Admin');
            } else {
                setShowSuccessModal('Unknown Role');
            }
        } catch (error) {
            if (error.response && error.response.status === 401) {
                // 401 Unauthorized: Incorrect password
                setErrorMessage('Incorrect password');
            } else {
                // Handle other errors
                console.error('Login failed:', error);
                setErrorMessage('Login failed. Please try again.');
            }
        }
    };

    return (
        <Container>
            <Row className="justify-content-center align-items-center min-vh-100">
                <Col md={6}>
                    <div className="login-box">
                        <Row>
                            <Col lg={6} md={4}>
                                <Image src={chef} roundedCircle />
                            </Col>
                        </Row>
                        <Form onSubmit={handleSubmit}>
                            <Form.Group controlId="formEmail">
                                <Form.Label>Email</Form.Label>
                                <Form.Control
                                    type="email"
                                    placeholder="Enter email"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                />
                            </Form.Group>

                            <Form.Group controlId="formPassword">
                                <Form.Label>Password</Form.Label>
                                <Form.Control
                                    type="password"
                                    placeholder="Password"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                />
                            </Form.Group>
                            {errorMessage && (
                                <div className="alert alert-danger">{errorMessage}</div>
                            )}
                            <Button className="mt-2" variant="primary" type="submit">
                                Login
                            </Button>
                        </Form>
                        <Modal show={showSuccessModal} onHide={handleResetForm}>
                            <Modal.Header closeButton>
                                <Modal.Title>Success!</Modal.Title>
                            </Modal.Header>
                            <Modal.Body>
                                {roleId === 300 ? 'Welcome Customer' : 'Welcome Admin'}
                            </Modal.Body>
                            <Modal.Footer>
                                <Button variant="primary" onClick={handleResetForm}>
                                    Close
                                </Button>
                            </Modal.Footer>
                        </Modal>
                    </div>
                </Col>
            </Row>
        </Container>
    );
}

export default Login;
